options.customCss:
:root {
	--font-family: 'Open Sans', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-size-body: 12px;
	--font-size-label: 14px;
	--font-size-test-notice: 12px;
	--font-size-input: 14px;
	--font-size-header: 20px;
	--font-size-paragraph: 14px;
	--font-size-link: 12px;
	--font-size-error: 12px;
	--font-size-html-h1: 28px;
	--font-size-html-h2: 20px;
	--font-size-html-h3: 16px;
	--font-size-html-h4: 14px;
	--font-size-html-h5: 12px;
	--font-size-html-h6: 10px;
	--font-size-subheader: 14px;

	/* Colors - Purple Theme */
	--color-background: #0a0b14;
	--color-test-notice-text: #a855f7;
	--color-test-notice-bg: #1e1b3a;
	--color-test-notice-border: #4c1d95;
	--color-card-bg: rgba(255, 255, 255, 0.05);
	--color-card-border: rgba(139, 92, 246, 0.3);
	--color-card-shadow: rgba(139, 92, 246, 0.15);
	--color-link: #a855f7;
	--color-header: #ffffff;
	--color-label: #e5e7eb;
	--color-input-border: rgba(139, 92, 246, 0.4);
	--color-input-text: #ffffff; /* Default white for dropdowns */
	--color-input-text-dark: #000000; /* Black for text inputs */
	--color-focus-border: #8b5cf6;
	--color-submit-btn-bg:#a855f7;
	--color-submit-btn-text: #ffffff;
	--color-error: #ef4444;
	--color-required: #f59e0b;
	--color-clear-button-bg: #6b7280;
	--color-html-text: #e5e7eb;
	--color-html-link: #a855f7;
	--color-header-subtext: #9ca3af;

	/* Border Radii */
	--border-radius-card: 12px;
	--border-radius-input: 8px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 12px;

	/* Spacing */
	--padding-container-top: 32px;
	--padding-card: 32px;
	--padding-test-notice-vertical: 16px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 20px;
	--padding-form-input: 16px;
	--card-padding: 32px;
	--card-margin-bottom: 20px;

	/* Dimensions */
	--container-width: 448px;
	--submit-btn-height: 52px;
	--checkbox-size: 20px;

	/* Others */
	--box-shadow-card: 0px 8px 32px 0px rgba(139, 92, 246, 0.12);
	--opacity-placeholder: 0.6;
}

/* Text inputs (Name & Email) - Black text */
input[type="text"], 
input[type="email"] {
	background: rgba(255, 255, 255, 0.95);
	border: 2px solid rgba(139, 92, 246, 0.3);
	color: var(--color-input-text-dark) !important; /* Black text */
	transition: all 0.3s ease;
}

input[type="text"]:focus, 
input[type="email"]:focus {
	border-color: var(--color-focus-border);
	box-shadow: 0 0 0 3px rgba(139, 92, 246, 0.1);
	background: rgba(255, 255, 255, 1);
}

/* Dropdown inputs (Role & Duration) - White text */
select {
	background: rgba(255, 255, 255, 0.05);
	border: 2px solid rgba(139, 92, 246, 0.3);
	color: var(--color-input-text) !important; /* White text */
	transition: all 0.3s ease;
}

select:focus {
	border-color: var(--color-focus-border);
	box-shadow: 0 0 0 3px rgba(139, 92, 246, 0.1);
	background: rgba(255, 255, 255, 0.08);
}

/* Placeholder text styling */
input[type="text"]::placeholder,
input[type="email"]::placeholder {
	color: rgba(0, 0, 0, 0.5); /* Dark placeholder for light backgrounds */
}

select option {
	background: #2d3748;
	color: white;
}

/* General input styling */
.input-field {
	transition: all 0.3s ease;
}

.submit-button {
	background: var(--color-submit-btn-bg);
	border: none;
	transition: all 0.3s ease;
	box-shadow: 0 4px 16px rgba(139, 92, 246, 0.3);
}

.submit-button:hover {
	transform: translateY(-2px);
	box-shadow: 0 6px 24px rgba(139, 92, 246, 0.4);
}
