options.customCss:
:root {
	/* Typography */
	--font-family: 'Inter', 'Segoe UI', -apple-system, sans-serif;
	--font-weight-normal: 400;
	--font-weight-medium: 500;
	--font-weight-bold: 600;
	--font-size-body: 14px;
	--font-size-label: 15px;
	--font-size-test-notice: 13px;
	--font-size-input: 15px;
	--font-size-header: 24px;
	--font-size-paragraph: 15px;
	--font-size-link: 14px;
	--font-size-error: 13px;
	--font-size-html-h1: 32px;
	--font-size-html-h2: 24px;
	--font-size-html-h3: 20px;
	--font-size-html-h4: 16px;
	--font-size-html-h5: 14px;
	--font-size-html-h6: 12px;
	--font-size-subheader: 16px;

	/* Light Green Real Estate Theme Colors */
	--color-background: #f8fdf8;
	--color-primary: #22c55e;
	--color-primary-light: #dcfce7;
	--color-primary-dark: #16a34a;
	--color-secondary: #059669;

	/* Notice/Alert Colors */
	--color-test-notice-text: #16a34a;
	--color-test-notice-bg: #f0fdf4;
	--color-test-notice-border: #bbf7d0;

	/* Card & Container Colors */
	--color-card-bg: #ffffff;
	--color-card-border: #d1fae5;
	--color-card-shadow: rgba(34, 197, 94, 0.08);

	/* Text Colors */
	--color-header: #14532d;
	--color-header-subtext: #166534;
	--color-label: #374151;
	--color-html-text: #374151;
	--color-link: #16a34a;
	--color-html-link: #22c55e;

	/* Form Input Colors */
	--color-input-border: #d1fae5;
	--color-input-text: #374151;
	--color-input-bg: #ffffff;
	--color-focus-border: #22c55e;
	--color-focus-shadow: rgba(34, 197, 94, 0.2);

	/* Button Colors */
	--color-submit-btn-bg: #22c55e;
	--color-submit-btn-text: #ffffff;
	--color-submit-btn-hover: #16a34a;
	--color-clear-button-bg: #6b7280;

	/* Status Colors */
	--color-error: #dc2626;
	--color-required: #ef4444;
	--color-success: #16a34a;
	--color-warning: #f59e0b;

	/* Border Radii - Modern & Professional */
	--border-radius-card: 12px;
	--border-radius-input: 8px;
	--border-radius-button: 8px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 12px;

	/* Spacing - Real Estate Professional */
	--padding-container-top: 32px;
	--padding-card: 32px;
	--padding-test-notice-vertical: 16px;
	--padding-test-notice-horizontal: 20px;
	--margin-bottom-card: 24px;
	--padding-form-input: 14px;
	--padding-form-textarea: 16px;
	--card-padding: 32px;
	--card-margin-bottom: 24px;
	--spacing-section: 24px;
	--spacing-field: 16px;

	/* Dimensions - Optimized for Forms */
	--container-width: 560px;
	--container-max-width: 90%;
	--submit-btn-height: 52px;
	--input-height: 48px;
	--textarea-min-height: 120px;
	--checkbox-size: 20px;
	--radio-size: 18px;

	/* Shadows & Effects */
	--box-shadow-card: 0px 6px 24px 0px var(--color-card-shadow);
	--box-shadow-input-focus: 0 0 0 3px var(--color-focus-shadow);
	--box-shadow-button: 0 2px 8px rgba(34, 197, 94, 0.15);
	--box-shadow-button-hover: 0 4px 12px rgba(34, 197, 94, 0.25);

	/* Transitions */
	--transition-default: all 0.2s ease-in-out;
	--transition-button: all 0.15s ease-in-out;

	/* Others */
	--opacity-placeholder: 0.6;
	--opacity-disabled: 0.5;
	--line-height-body: 1.5;
	--line-height-header: 1.3;
}

/* Additional Real Estate Form Styling */
.form-container {
	background: linear-gradient(135deg, var(--color-background) 0%, #ffffff 100%);
	min-height: 100vh;
	padding: var(--padding-container-top) 20px;
}

.form-card {
	background: var(--color-card-bg);
	border: 1px solid var(--color-card-border);
	border-radius: var(--border-radius-card);
	box-shadow: var(--box-shadow-card);
	padding: var(--padding-card);
	max-width: var(--container-width);
	margin: 0 auto;
	position: relative;
}

.form-header {
	text-align: center;
	margin-bottom: var(--spacing-section);
	padding-bottom: 20px;
	border-bottom: 2px solid var(--color-primary-light);
}

.form-title {
	color: var(--color-header);
	font-size: var(--font-size-header);
	font-weight: var(--font-weight-bold);
	margin-bottom: 8px;
	line-height: var(--line-height-header);
}

.form-subtitle {
	color: var(--color-header-subtext);
	font-size: var(--font-size-subheader);
	font-weight: var(--font-weight-normal);
	line-height: var(--line-height-body);
}

.form-section {
	margin-bottom: var(--spacing-section);
}

.form-input, .form-textarea, .form-select {
	border: 2px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	padding: var(--padding-form-input);
	font-size: var(--font-size-input);
	color: var(--color-input-text);
	background: var(--color-input-bg);
	transition: var(--transition-default);
	width: 100%;
	min-height: var(--input-height);
}

.form-input:focus, .form-textarea:focus, .form-select:focus {
	border-color: var(--color-focus-border);
	box-shadow: var(--box-shadow-input-focus);
	outline: none;
}

.submit-button {
	background: var(--color-submit-btn-bg);
	color: var(--color-submit-btn-text);
	border: none;
	border-radius: var(--border-radius-button);
	padding: 16px 32px;
	font-size: var(--font-size-input);
	font-weight: var(--font-weight-medium);
	height: var(--submit-btn-height);
	width: 100%;
	cursor: pointer;
	transition: var(--transition-button);
	box-shadow: var(--box-shadow-button);
}

.submit-button:hover {
	background: var(--color-submit-btn-hover);
	box-shadow: var(--box-shadow-button-hover);
	transform: translateY(-1px);
}
